import React, { useState } from 'react';

interface VideoCardProps {
    song: {
        videoId: string;
        title: string;
        vtuberName: string;
        publishedAt: string;
        viewCount: number;
    };
}

const VideoCard: React.FC<VideoCardProps> = ({ song }) => {
    const thumbnailUrl = `https://img.youtube.com/vi/${song.videoId}/0.jpg`; // YouTube 썸네일 URL 형식
    const [isHovered, setIsHovered] = useState(false); // hover 상태

    const handleMouseEnter = () => setIsHovered(true); // 마우스를 올리면 상세 정보 표시
    const handleMouseLeave = () => setIsHovered(false); // 마우스를 떼면 상세 정보 숨김

    const handleCardClick = () => {
        window.open(`https://www.youtube.com/watch?v=${song.videoId}`, '_blank');
    };

    return (
        <div
            className="video-card"
            onClick={handleCardClick}
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
        >
            <img src={thumbnailUrl} alt={song.title} className="thumbnail" />
            <div className="video-info">
                <h3>{song.title}</h3>
                {/* 마우스가 올라갔을 때만 상세 정보 표시 */}
                {isHovered && (
                    <div className="hover-details">
                        <p>채널명: {song.vtuberName}</p>
                        <p>조회수: {song.viewCount.toLocaleString()}</p>
                        <p>게시일: {new Date(song.publishedAt).toLocaleDateString()}</p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default VideoCard;
