import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom'; // 페이지 이동을 위해 useNavigate 사용

interface VideoCardProps {
    song: {
        videoId: string;
        title: string;
        vtuberName: string;
        publishedAt: string;
        viewCount: number;
    };
}

const VideoCard: React.FC<VideoCardProps> = ({ song }) => {
    const [hovered, setHovered] = useState(false);
    const navigate = useNavigate(); // 페이지 이동을 위한 훅

    const thumbnailUrl = `https://img.youtube.com/vi/${song.videoId}/0.jpg`; // YouTube 썸네일 URL

    const handleMouseEnter = () => {
        setHovered(true);
    };

    const handleMouseLeave = () => {
        setHovered(false);
    };

    // 제목을 클릭했을 때 제목으로 검색창 열기
    const handleTitleClick = () => {
        navigate(`/search?query=${encodeURIComponent(song.title)}`);
    };

    // 채널명을 클릭했을 때 채널명으로 검색창 열기
    const handleChannelClick = () => {
        navigate(`/search?query=${encodeURIComponent(song.vtuberName)}`);
    };

    return (
        <div
            className="video-card"
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
        >
            {/* 썸네일과 제목 (기본 상태) */}
            {!hovered ? (
                <div className="thumbnail-content">
                    <img src={thumbnailUrl} alt={song.title} className="thumbnail" />
                    <h3 onClick={handleTitleClick} style={{ cursor: 'pointer', textDecoration: 'underline' }}>
                        {song.title}
                    </h3>
                </div>
            ) : (
                <div className="hover-content">
                    <h3 onClick={handleTitleClick} style={{ cursor: 'pointer', textDecoration: 'underline' }}>
                        {song.title}
                    </h3>
                    <p onClick={handleChannelClick} style={{ cursor: 'pointer', color: 'blue', textDecoration: 'underline' }}>
                        채널명: {song.vtuberName}
                    </p>
                    <p>조회수: {song.viewCount.toLocaleString()}</p>
                    <p>게시일: {new Date(song.publishedAt).toLocaleDateString()}</p>
                </div>
            )}
        </div>
    );
};

export default VideoCard;
