import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './MainPage.css'; // MainPage 전용 CSS 파일
import SearchBar from './components/searchBar'; // 검색창 컴포넌트
import VideoCard from './components/VideoCard'; // VideoCard 컴포넌트 가져오기
import { useNavigate } from 'react-router-dom'; // 페이지 전환을 위해 사용

const MainPage: React.FC = () => {
    const [data, setData] = useState<any>(null);
    const navigate = useNavigate();

    useEffect(() => {
        axios.get('/main')
            .then((response) => {
                console.log(response.data);
                setData(response.data);
            })
            .catch((error) => {
                console.error('백엔드에서 데이터 가져오기 오류:', error);
            });
    }, []);

    const handleSearch = (searchTerm: string) => {
        if (searchTerm.trim() !== '') {
            navigate(`/search?query=${searchTerm}`);
        }
    };

    if (!data) {
        return <p>로딩 중...</p>;
    }

    return (
        <div className="main-page">
            <h1>버튜버 노래</h1>

            {/* 검색창 */}
            <SearchBar onSearch={handleSearch} /> {/* onSearch prop 전달 */}

            <h2>랜덤 노래</h2>
            <div className="video-grid">
                {data.randomSongs.map((song: any) => (
                    <VideoCard key={song.id} song={song} />
                ))}
            </div>

            <h2>주간 인기 노래</h2>
            <div className="video-grid">
                {data.top10WeeklySongs.map((song: any) => (
                    <VideoCard key={song.id} song={song} />
                ))}
            </div>

            <h2>일간 인기 노래</h2>
            <div className="video-grid">
                {data.top10DailySongs.map((song: any) => (
                    <VideoCard key={song.id} song={song} />
                ))}
            </div>

            <h2>최신 노래</h2>
            <div className="video-grid">
                {data.top10RecentSongs.map((song: any) => (
                    <VideoCard key={song.id} song={song} />
                ))}
            </div>
        </div>
    );
};

export default MainPage;
