import React from 'react';

interface VideoCardProps {
    song: {
        videoId: string;
        title: string;
        vtuberName: string;
        publishedAt: string;
        viewCount: number;
    };
}

const VideoCard: React.FC<VideoCardProps> = ({ song }) => {
    const thumbnailUrl = `https://img.youtube.com/vi/${song.videoId}/0.jpg`;

    const handleCardClick = () => {
        window.open(`https://www.youtube.com/watch?v=${song.videoId}`, '_blank');
    };

    return (
        <div className="video-card" onClick={handleCardClick}>
            <img
                src={thumbnailUrl}
                alt={song.title}
                className="thumbnail"
                onError={(e: any) => { e.target.src = "/images/default_thumbnail.png"; }} // 기본 이미지 처리
            />
            <div className="video-info">
                <h3>{song.title}</h3>
                <p>채널명: {song.vtuberName}</p>
                <p>게시일: {new Date(song.publishedAt).toLocaleDateString()}</p>
                <p>조회수: {song.viewCount.toLocaleString()}</p>
            </div>
        </div>
    );
};

export default VideoCard;
