import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import Header from './page/components/Header';
import MainPage from './page/MainPage';
import SearchResultsPage from './page/SearchResultsPage';
import WeeklyChart from './page/components/WeeklyChart';
import axios from 'axios';

const App: React.FC = () => {
    const [top10WeeklySongs, setTop10WeeklySongs] = useState<any[]>([]);

    useEffect(() => {
        // 주간 인기 차트 데이터 가져오기
        axios.get('http://localhost:8080/api/v1/weekly-chart')
            .then((response) => {
                setTop10WeeklySongs(response.data || []); // 데이터가 없을 경우 빈 배열 설정
            })
            .catch((error) => {
                console.error('주간 인기 차트 데이터를 가져오는 중 오류 발생:', error);
            });
    }, []);

    return (
        <Router>
            <div className="app-container">
                <Header />
                <div className="main-layout">
                    <div className="content">
                        <Routes>
                            <Route path="/" element={<MainPage />} />
                            <Route path="/search" element={<SearchResultsPage />} />
                        </Routes>
                    </div>
                    <div className="sidebar">
                        {/* 모든 페이지에서 동일한 주간 인기 차트를 렌더링 */}
                        <WeeklyChart top10WeeklySongs={top10WeeklySongs} />
                    </div>
                </div>
            </div>
        </Router>
    );
};

export default App;
