import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './MainPage.css'; // MainPage 전용 CSS 파일
import VideoCard from './components/VideoCard'; // VideoCard 컴포넌트 가져오기
import { useNavigate } from 'react-router-dom'; // 페이지 전환을 위해 사용

const MainPage: React.FC = () => {
    const [data, setData] = useState<any>(null);
    const navigate = useNavigate();

    useEffect(() => {
        axios.get('/main')
            .then((response) => {
                console.log(response.data);
                setData(response.data);
            })
            .catch((error) => {
                console.error('백엔드에서 데이터 가져오기 오류:', error);
            });
    }, []);

    const handleSearch = (searchTerm: string) => {
        if (searchTerm.trim() !== '') {
            navigate(`/search?query=${searchTerm}`);
        }
    };

    if (!data) {
        return <p>로딩 중...</p>;
    }

    return (
        <div className="main-page">
            <section className="latest-songs">
                <h2>최신 노래</h2>
                <div className="song-grid">
                    {data.top10RecentSongs.map((song: any) => (
                        <VideoCard key={song.id} song={song} />
                    ))}
                </div>
            </section>

            <section className="daily-popular">
                <h2>일간 인기 노래</h2>
                <div className="song-grid">
                    {data.top10DailySongs.map((song: any) => (
                        <VideoCard key={song.id} song={song} />
                    ))}
                </div>
            </section>

            <section className="weekly-chart">
                <h2>주간 인기 차트</h2>
                <div className="chart-container">
                    {data.top10WeeklySongs.map((song: any, index: number) => (
                        <div className="chart-item" key={song.id}>
                            <span>{index + 1}. {song.title}</span>
                        </div>
                    ))}
                </div>
            </section>
        </div>
    );
};

export default MainPage;
