import React, { useEffect, useState } from 'react';
import axios from 'axios';
import SearchBar from './components/searchBar'; // 검색창 컴포넌트 import
import './MainPage.css'; // CSS 파일 import
import { useNavigate } from 'react-router-dom'; // React Router DOM 사용

const MainPage: React.FC = () => {
    const [data, setData] = useState<any>(null);
    const navigate = useNavigate(); // 검색 결과 페이지로 이동을 위한 hook

    useEffect(() => {
        axios.get('/main')
            .then((response) => {
                console.log(response.data);
                setData(response.data);
            })
            .catch((error) => {
                console.error('백엔드에서 데이터 가져오기 오류:', error);
            });
    }, []);

    // 검색을 처리하고 검색 결과 페이지로 이동
    const handleSearch = (searchTerm: string) => {
        if (searchTerm.trim() !== '') {
            navigate(`/search?query=${searchTerm}`); // 검색어를 쿼리 파라미터로 검색 결과 페이지로 이동
        }
    };

    if (!data) {
        return <p>로딩 중...</p>;
    }

    return (
        <div className="main-page">
            <h1>버튜버 노래</h1>

            {/* 검색창 */}
            <SearchBar onSearch={handleSearch} />

            {/* 랜덤 노래 섹션 */}
            <h2>랜덤 노래</h2>
            <div className="video-grid">
                {data.randomSongs.map((song: any) => (
                    <VideoCard key={song.id} song={song} />
                ))}
            </div>

            {/* 주간 인기 노래 섹션 */}
            <h2>주간 인기 노래</h2>
            <div className="video-grid">
                {data.top10WeeklySongs.map((song: any) => (
                    <VideoCard key={song.id} song={song} />
                ))}
            </div>

            {/* 일간 인기 노래 섹션 */}
            <h2>일간 인기 노래</h2>
            <div className="video-grid">
                {data.top10DailySongs.map((song: any) => (
                    <VideoCard key={song.id} song={song} />
                ))}
            </div>

            {/* 최신 노래 섹션 */}
            <h2>최신 노래</h2>
            <div className="video-grid">
                {data.top10RecentSongs.map((song: any) => (
                    <VideoCard key={song.id} song={song} />
                ))}
            </div>
        </div>
    );
};

// 각 노래 정보를 표시하는 VideoCard 컴포넌트
const VideoCard: React.FC<{ song: any }> = ({ song }) => {
    const thumbnailUrl = `https://img.youtube.com/vi/${song.videoId}/0.jpg`; // YouTube 썸네일 URL 형식

    const handleCardClick = () => {
        window.open(`https://www.youtube.com/watch?v=${song.videoId}`, '_blank');
    };

    return (
        <div className="video-card" onClick={handleCardClick}>
            <img src={thumbnailUrl} alt={song.title} className="thumbnail" />
            <div className="video-info">
                <h3>{song.title}</h3>
                <p>채널명: {song.vtuberName}</p>
                <p>게시일: {new Date(song.publishedAt).toLocaleDateString()}</p>
                <p>조회수: {song.viewCount.toLocaleString()}</p>
            </div>
        </div>
    );
};

export default MainPage;
