import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';  // 페이지 이동을 위한 useNavigate 훅 사용
import './Header.css';  // 스타일 정의

const Header: React.FC = () => {
    const [searchTerm, setSearchTerm] = useState('');
    const navigate = useNavigate();

    // 검색 기능 처리
    const handleSearch = () => {
        if (searchTerm.trim() === '') return; // 검색어가 없을 경우 검색하지 않음
        navigate(`/search?query=${searchTerm}`);
    };

    // 검색어 입력 후 엔터를 눌렀을 때 처리
    const handleKeyPress = (event: React.KeyboardEvent<HTMLInputElement>) => {
        if (event.key === 'Enter') {
            handleSearch();
        }
    };

    // 검색어 초기화 처리
    const handleClear = () => {
        setSearchTerm(''); // X 버튼을 누르면 검색어를 초기화
    };

    // 홈으로 돌아가는 처리
    const handleGoHome = () => {
        navigate('/');  // 홈 경로로 이동
    };

    return (
        <header className="header">
            <button className="home-btn" onClick={handleGoHome}>
                홈
            </button>

            <div className="search-bar">
                <input
                    type="text"
                    placeholder="검색"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    onKeyPress={handleKeyPress} // Enter 키 입력 시 검색
                />
                {searchTerm && (
                    <button type="button" className="clear-btn" onClick={handleClear}>
                        &#x2715; {/* X 아이콘 */}
                    </button>
                )}
                <button type="submit" className="search-btn" onClick={handleSearch}>
                    <img src="/images/SearchBar.png" alt="Search" />
                </button>
            </div>
        </header>
    );
};

export default Header;
