import React, { useState } from 'react';
import './SearchBar.css'; // 스타일 import

interface SearchBarProps {
    onSearch: (searchTerm: string) => void; // 검색을 처리할 함수 prop 정의
}

const SearchBar: React.FC<SearchBarProps> = ({ onSearch }) => {
    const [searchTerm, setSearchTerm] = useState('');

    const handleClear = () => {
        setSearchTerm('');
        onSearch(''); // 검색어를 초기화할 때도 부모 컴포넌트에 알림
    };

    const handleKeyDown = (event: React.KeyboardEvent) => {
        if (event.key === 'Enter') {
            onSearch(searchTerm); // 엔터 키를 눌렀을 때 검색어를 부모 컴포넌트로 전달
        }
    };

    return (
        <div className="search-bar">
            <input
                type="text"
                placeholder="검색어를 입력하세요"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                onKeyDown={handleKeyDown} // 엔터 키 이벤트 처리
            />
            <button type="button" className="clear-btn" onClick={handleClear}>
                &#x2715;
            </button>
            <button type="submit" className="search-btn" onClick={() => onSearch(searchTerm)}>
                <img src="/images/SearchBar.png" alt="Search" />
            </button>
        </div>
    );
};

export default SearchBar;
