import React, { useEffect, useState } from 'react';
import axios from 'axios';
import SearchBar from './components/searchBar'; // 검색창 컴포넌트 import
import './MainPage.css'; // CSS 파일 import

const MainPage: React.FC = () => {
    const [data, setData] = useState<any>(null);
    const [searchResults, setSearchResults] = useState<{ songs: any[], vtubers: any[] } | null>(null);

    useEffect(() => {
        axios.get('/main')
            .then((response) => {
                console.log(response.data);
                setData(response.data);
            })
            .catch((error) => {
                console.error('백엔드에서 데이터 가져오기 오류:', error);
            });
    }, []);

    const handleSearch = async (searchTerm: string) => {
        if (searchTerm.trim() === '') {
            setSearchResults(null); // 검색어가 없으면 검색 결과를 초기화
            return;
        }

        console.log('Sending search request for:', searchTerm); // 디버그 로그

        try {
            const response = await axios.get('http://localhost:8080/api/v1/vtubers/search', {
                params: { query: searchTerm }
            });
            console.log('Search results:', response.data); // 디버그 로그
            setSearchResults(response.data); // 검색 결과를 state에 저장
        } catch (error) {
            console.error('검색 중 오류 발생:', error);
        }
    };

    if (!data) {
        return <p>로딩 중...</p>;
    }

    return (
        <div className="main-page">
            <h1>버튜버 노래</h1>

            {/* 검색창 */}
            <SearchBar onSearch={handleSearch} />

            {/* 검색 결과 섹션 */}
            {searchResults ? (
                <div>
                    <h2>검색 결과 - 노래</h2>
                    <div className="video-grid">
                        {searchResults.songs.map((song: any) => (
                            <VideoCard key={song.id} song={song} />
                        ))}
                    </div>

                    <h2>검색 결과 - 버튜버</h2>
                    <div className="vtuber-grid">
                        {searchResults.vtubers.map((vtuber: any) => (
                            <VtuberCard key={vtuber.id} vtuber={vtuber} />
                        ))}
                    </div>
                </div>
            ) : (
                <>
                    {/* 랜덤 노래 섹션 */}
                    <h2>랜덤 노래</h2>
                    <div className="video-grid">
                        {data.randomSongs.map((song: any) => (
                            <VideoCard key={song.id} song={song} />
                        ))}
                    </div>

                    {/* 주간 인기 노래 섹션 */}
                    <h2>주간 인기 노래</h2>
                    <div className="video-grid">
                        {data.top10WeeklySongs.map((song: any) => (
                            <VideoCard key={song.id} song={song} />
                        ))}
                    </div>

                    {/* 일간 인기 노래 섹션 */}
                    <h2>일간 인기 노래</h2>
                    <div className="video-grid">
                        {data.top10DailySongs.map((song: any) => (
                            <VideoCard key={song.id} song={song} />
                        ))}
                    </div>

                    {/* 최신 노래 섹션 */}
                    <h2>최신 노래</h2>
                    <div className="video-grid">
                        {data.top10RecentSongs.map((song: any) => (
                            <VideoCard key={song.id} song={song} />
                        ))}
                    </div>
                </>
            )}
        </div>
    );
};

// 각 노래 정보를 표시하는 VideoCard 컴포넌트
const VideoCard: React.FC<{ song: any }> = ({ song }) => {
    const thumbnailUrl = `https://img.youtube.com/vi/${song.videoId}/0.jpg`; // YouTube 썸네일 URL 형식

    const handleCardClick = () => {
        window.open(`https://www.youtube.com/watch?v=${song.videoId}`, '_blank');
    };

    return (
        <div className="video-card" onClick={handleCardClick}>
            <img src={thumbnailUrl} alt={song.title} className="thumbnail" />
            <div className="video-info">
                <h3>{song.title}</h3>
                <p>채널명: {song.vtuberName}</p>
                <p>게시일: {new Date(song.publishedAt).toLocaleDateString()}</p>
                <p>조회수: {song.viewCount.toLocaleString()}</p>
            </div>
        </div>
    );
};

// 각 버튜버 정보를 표시하는 VtuberCard 컴포넌트
const VtuberCard: React.FC<{ vtuber: any }> = ({ vtuber }) => {
    return (
        <div className="vtuber-card">
            <h3>{vtuber.name}</h3>
            <p>채널 ID: {vtuber.channelId}</p>
        </div>
    );
};

export default MainPage;
