import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './MainPage.css'; // MainPage 전용 CSS 파일
import VideoCard from './components/VideoCard'; // 비디오 카드 컴포넌트

const MainPage: React.FC = () => {
    const [data, setData] = useState<any>({
        latestSongs: [],         // 초기값 빈 배열
        dailyPopularSongs: [],    // 초기값 빈 배열
        weeklyTopSongs: []        // 초기값 빈 배열
    });
    const [isLoading, setIsLoading] = useState(true); // 로딩 상태 추가

    useEffect(() => {
        axios.get('/main') // 백엔드에서 데이터 가져오기
            .then((response) => {
                console.log(response.data);
                setData({
                    latestSongs: response.data.latestSongs || [],  // 데이터가 없으면 빈 배열을 기본값으로 설정
                    dailyPopularSongs: response.data.dailyPopularSongs || [],  // 데이터가 없으면 빈 배열
                    weeklyTopSongs: response.data.weeklyTopSongs || []  // 데이터가 없으면 빈 배열
                });
                setIsLoading(false); // 로딩 완료
            })
            .catch((error) => {
                console.error('백엔드에서 데이터 가져오기 오류:', error);
                setIsLoading(false); // 오류 발생 시에도 로딩 완료 상태로 전환
            });
    }, []);

    // 로딩 중일 때 표시할 UI
    if (isLoading) {
        return <p>로딩 중...</p>;
    }

    // 데이터가 없을 때 표시할 UI
    if (!data || (!data.latestSongs.length && !data.dailyPopularSongs.length && !data.weeklyTopSongs.length)) {
        return <p>데이터가 없습니다.</p>;
    }

    return (
        <div className="main-page">
            {/* 최신 노래 섹션 */}
            <section>
                <h2>최신 노래</h2>
                <div className="video-grid">
                    {data.latestSongs.length > 0 ? (
                        data.latestSongs.map((song: any) => (
                            <VideoCard key={song.id} song={song} />
                        ))
                    ) : (
                        <p>최신 노래가 없습니다.</p>
                    )}
                </div>
            </section>

            {/* 인기 있어요 섹션 */}
            <section>
                <h2>인기 있어요</h2>
                <div className="popular-grid">
                    {data.dailyPopularSongs.length > 0 ? (
                        data.dailyPopularSongs.map((song: any) => (
                            <VideoCard key={song.id} song={song} />
                        ))
                    ) : (
                        <p>인기 있는 노래가 없습니다.</p>
                    )}
                </div>
            </section>

            {/* 주간 인기차트 섹션 */}
            <section className="chart-section">
                <h2>주간 인기차트</h2>
                <ul className="chart-list">
                    {data.weeklyTopSongs.length > 0 ? (
                        data.weeklyTopSongs.map((song: any, index: number) => (
                            <li key={song.id} className="chart-item">
                                <h4>{index + 1}. {song.title}</h4>
                                <span>{song.artist}</span>
                            </li>
                        ))
                    ) : (
                        <p>주간 인기 차트가 없습니다.</p>
                    )}
                </ul>
            </section>
        </div>
    );
};

export default MainPage;
