import React, { useState } from 'react';
import './SearchBar.css'; // 스타일 정의

interface SearchBarProps {
    onSearch: (searchTerm: string) => void;
}

const SearchBar: React.FC<SearchBarProps> = ({ onSearch }) => {
    const [searchTerm, setSearchTerm] = useState('');

    const handleSearch = () => {
        if (searchTerm.trim() === '') return; // 검색어가 없을 경우 검색하지 않음
        onSearch(searchTerm);
    };

    // Enter 키를 눌렀을 때 검색
    const handleKeyPress = (event: React.KeyboardEvent<HTMLInputElement>) => {
        if (event.key === 'Enter') {
            handleSearch();
        }
    };

    const handleClear = () => {
        setSearchTerm('');
    };

    return (
        <div className="search-bar">
            <input
                type="text"
                placeholder="검색어를 입력하세요"
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                onKeyPress={handleKeyPress} // Enter 키 입력 시 검색
            />
            <button type="button" className="clear-btn" onClick={handleClear}>
                &#x2715; {/* X 아이콘 */}
            </button>
            <button type="submit" className="search-btn" onClick={handleSearch}>
                <img src="/images/SearchBar.png" alt="Search" />
            </button>
        </div>
    );
};

export default SearchBar;
