import React from 'react';
import { useNavigate } from 'react-router-dom'; // 검색 페이지로 이동하기 위해 사용

interface VideoCardProps {
    song: {
        videoId: string;
        title: string;
        vtuberName: string;
        publishedAt: string;
        viewCount: number;
    };
}

const VideoCard: React.FC<VideoCardProps> = ({ song }) => {
    const thumbnailUrl = `https://img.youtube.com/vi/${song.videoId}/0.jpg`; // YouTube 썸네일 URL
    const navigate = useNavigate(); // 페이지 이동을 위해 사용

    // 제목을 클릭했을 때 제목으로 검색하는 함수
    const handleTitleClick = () => {
        navigate(`/search?query=${encodeURIComponent(song.title)}`);
    };

    // 채널명을 클릭했을 때 채널명으로 검색하는 함수
    const handleChannelClick = () => {
        navigate(`/search?query=${encodeURIComponent(song.vtuberName)}`);
    };

    return (
        <div className="video-card">
            {/* 썸네일과 제목 (기본 상태) */}
            <div className="thumbnail-content">
                <img src={thumbnailUrl} alt={song.title} className="thumbnail" />
                <h3 onClick={handleTitleClick} style={{ cursor: 'pointer', textDecoration: 'underline' }}>{song.title}</h3>
            </div>

            {/* 마우스 오버 시 보여줄 상세 정보 */}
            <div className="hover-content">
                <h3 onClick={handleTitleClick} style={{ cursor: 'pointer', textDecoration: 'underline' }}>{song.title}</h3>
                <p onClick={handleChannelClick} style={{ cursor: 'pointer', color: 'blue', textDecoration: 'underline' }}>
                    채널명: {song.vtuberName}
                </p>
                <p>조회수: {song.viewCount.toLocaleString()}</p>
                <p>게시일: {new Date(song.publishedAt).toLocaleDateString()}</p>
            </div>
        </div>
    );
};

export default VideoCard;
