import React, { useState } from 'react';
import axios from 'axios';
import './SearchBar.css'; // 스타일 import

const SearchBar: React.FC = () => {
    const [searchTerm, setSearchTerm] = useState(''); // 검색어 상태
    const [searchResults, setSearchResults] = useState([]); // 검색 결과 상태
    const [noResults, setNoResults] = useState(false); // 검색 결과 없음 상태

    // 검색 처리 함수
    const handleSearch = async () => {
        if (searchTerm.trim() === '') return;

        try {
            const response = await axios.get(`/api/v1/songs/search`, {
                params: { query: searchTerm }
            });
            const results = response.data;

            // 검색 결과가 있을 때만 설정
            if (results.length > 0) {
                setSearchResults(results);
                setNoResults(false); // 검색 결과 있음
            } else {
                setSearchResults([]);
                setNoResults(true); // 검색 결과 없음
            }
        } catch (error) {
            console.error('검색 중 오류 발생:', error);
        }
    };

    // 검색어 초기화 처리 함수
    const handleClear = () => {
        setSearchTerm('');
        setSearchResults([]); // 검색 결과 초기화
        setNoResults(false); // 메시지 제거
    };

    return (
        <div>
            <div className="search-bar">
                <input
                    type="text"
                    placeholder="검색어를 입력하세요"
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                />
                <button type="button" className="clear-btn" onClick={handleClear}>
                    &#x2715;
                </button>
                <button type="submit" className="search-btn" onClick={handleSearch}>
                    <img src="/images/SearchBar.png" alt="Search" />
                </button>
            </div>

            {/* 검색 결과 출력 */}
            {noResults && <p>검색 결과가 없습니다.</p>}

            {/* 검색 결과가 있을 때만 보여줌 */}
            {searchResults.length > 0 && (
                <div className="search-results">
                    <h2>검색 결과</h2>
                    <div className="video-grid">
                        {searchResults.map((song: any) => (
                            <div className="video-card" key={song.id}>
                                <img
                                    src={`https://img.youtube.com/vi/${song.videoId}/0.jpg`}
                                    alt={song.title}
                                    className="thumbnail"
                                />
                                <div className="video-info">
                                    <h3>{song.title}</h3>
                                    <p>채널명: {song.vtuberName}</p>
                                    <p>게시일: {new Date(song.publishedAt).toLocaleDateString()}</p>
                                    <p>조회수: {song.viewCount.toLocaleString()}</p>
                                </div>
                            </div>
                        ))}
                    </div>
                </div>
            )}
        </div>
    );
};

export default SearchBar;
